    // Testar se a array possui o tamanho minímo (1)
    @Test
    public void  is_min_size() {
        Soma minarr = new Soma();
        minarr.array = new int[]{1,2,3,4,5,6,7,8,9};
        if (minarr.getTamanho() < 1) {throw new AssertionError("A array nao e valida");}
    }
    @Test
    public void  is_not_min_size() {
        Soma minarr = new Soma();
        minarr.array = new int[]{};
        if (minarr.getTamanho() < 1) {
            throw new AssertionError("A array nao e valida");
        }
    }
